// components/Login.tsx
import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom' // 導入 useNavigate
import { useAuth } from '../context/AuthContext' // 確保導入 useAuth
const API_URL = 'http://localhost:8080'

const Login: React.FC = () => {
  const { setIsLoggedIn, setName } = useAuth() // 使用上下文
  const [email, setemail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
  const navigate = useNavigate()

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault()
    setError('')

    try {
      const response = await fetch(API_URL + '/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email,
          password,
        }),
      })

      const { jwt_token, username } = await response.json() // 獲取用戶名稱
      console.log(response.text())
      await localStorage.setItem('jwt_token', jwt_token)
      // await localStorage.setItem('name', username)
      setIsLoggedIn(true) // 設置登錄狀態
      setName(username) // 設置用戶名稱

      setemail('')
      setPassword('')
      navigate('/')
    } catch (error) {
      if (axios.isAxiosError(error) && error.response) {
        setError(error.response.data.message || '登入失敗')
      } else {
        setError('伺服器錯誤，請稍後再試')
      }
    }
  }

  return (
    <div>
      <h2>登入</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">用戶名:</label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={(e) => setemail(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">密碼:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">登入</button>
      </form>
    </div>
  )
}

export default Login
