// src/components/Home.tsx
import React from 'react'
import Widget from './widget'
import { useAuth } from '../context/AuthContext'

// test data
const activities = [
  {
    id: '1',
    name: '音乐节',
    start_date: new Date('2024-12-25T19:00:00'),
    end_date: new Date('2024-12-25T23:00:00'),
    location: '中央公园',
    description: '体验本年度最大的音乐盛会，尽情享受摇滚音乐与电子音乐的狂欢！',
    imageUrl: 'https://via.placeholder.com/300x200?text=Music+Festival', // 模拟的活动图片链接
  },
  {
    id: '2',
    name: '夏日马拉松',
    start_date: new Date('2024-12-30T07:00:00'),
    end_date: new Date('2024-12-30T12:00:00'),
    location: '海滨大道',
    description: '全程42公里的马拉松赛程，挑战自我，健身与健康同行。',
    imageUrl: 'https://via.placeholder.com/300x200?text=Marathon', // 模拟的活动图片链接
  },
  {
    id: '3',
    name: '美食节',
    start_date: new Date('2024-12-15T10:00:00'),
    end_date: new Date('2024-12-15T16:00:00'),
    location: '市区会展中心',
    description: '与全球各地的美食爱好者一起，尽享诱人的美食与饮品。',
    imageUrl: 'https://via.placeholder.com/300x200?text=Food+Festival', // 模拟的活动图片链接
  },
]

const Home: React.FC = () => {
  const { isLoggedIn } = useAuth()
  return (
    <div>
      <h1>歡迎來到首頁！</h1>
      {isLoggedIn ? (
        <p>您已經登入，享受您的體驗！</p>
      ) : (
        <p>請登入以獲得完整功能。</p>
      )}
      <div
        style={{
          display: 'flex',
          gap: '20px',
          flexWrap: 'wrap',
        }}
      >
        {activities.map((activity) => (
          <Widget
            key={activity.id}
            id={activity.id}
            name={activity.name}
            start_date={activity.start_date}
            end_date={activity.end_date}
            imageUrl={activity.imageUrl}
          />
        ))}
      </div>
    </div>
  )
}

export default Home
