// src/components/Home.tsx
import React from 'react'
import Widget from './widget'
import { useAuth } from '../context/AuthContext'
const API_URL = 'http://localhost:8080'

// test data
interface Region {
  region_name: string
  region_price: number
  region_capacity: number
}

interface Activity {
  _id: string
  on_sale_date: Date
  start_time: Date
  end_time: Date
  title: string
  content: string
  cover_img: { type: string; data: number[] }
  price_level_img: { type: string; data: number[] }
  arena_id: string
  creator_id: string
  is_archived: boolean
  regions: Region[]
}
const Home: React.FC = () => {
  const { isLoggedIn } = useAuth()
  const [activities, setActivities] = React.useState<Activity[]>([
    {
      _id: '5255f26e-894b-4532-9161-9b78ceec88ca',
      on_sale_date: new Date('2024-12-01'),
      start_time: new Date('2024-12-15 19:00:00'),
      end_time: new Date('2024-12-15 21:00:00'),
      title: 'Concert A',
      content: 'A wonderful evening of music.',
      cover_img: { type: 'cover_a.jpg', data: [] },
      price_level_img: { type: 'price_a.jpg', data: [] },
      arena_id: 'ec9c7e6c-00f4-4e11-9821-01910f109c40',
      creator_id: '1',
      is_archived: true,
      regions: [
        {
          region_name: 'A',
          region_price: 100,
          region_capacity: 100,
        },
        {
          region_name: 'B',
          region_price: 80,
          region_capacity: 200,
        },
      ],
    },
  ]) // 将活动数据存储到状态中
  const [arenaId, setArenaId] = React.useState('')
  const fetchActivities = async (arenaId: any) => {
    try {
      const queryParam = arenaId ? `?arena_id=${arenaId}` : ''
      const response = await fetch(`${API_URL}/activity/list${queryParam}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      })

      if (response.ok) {
        const data = await response.json()
        setActivities(data.activities) // 使用后端返回的 activities 数据
      } else {
        console.error('Failed to fetch activities:', response.statusText)
      }
    } catch (error) {
      console.error('Error fetching activities:', error)
    }
  }

  fetchActivities(arenaId) // 页面加载时获取活动数据

  return (
    <div>
      <h1>歡迎來到首頁！</h1>
      {isLoggedIn ? (
        <>
          {' '}
          <p>您已經登入，享受您的體驗！</p>
          <p>{activities[0]?.title} </p>
        </>
      ) : (
        <p>請登入以獲得完整功能。</p>
      )}
      <div
        style={{
          display: 'flex',
          gap: '20px',
          flexWrap: 'wrap',
        }}
      >
        {activities.map((activity) => (
          <Widget
            key={activity._id}
            id={activity._id}
            name={activity.title}
            start_date={activity.start_time}
            end_date={activity.end_time}
            imageUrl={activity.cover_img}
          />
        ))}
      </div>
    </div>
  )
}

export default Home
